import UIKit

protocol CurrencyPresentationLogic {
    func presentSomething(response: Currency.Something.Response)
}

class CurrencyPresenter: CurrencyPresentationLogic {
    weak var viewController: CurrencyDisplayLogic?
    
    func presentSomething(response: Currency.Something.Response) {
        var viewModel = Currency.Something.ViewModel()
        viewModel.name = response.userInfo?.name
        viewModel.accountID = response.userInfo?.accountID
        viewModel.balance = response.userInfo?.balance
        viewModel.userCurrency = response.userCurrency
        viewController?.displaySomething(viewModel: viewModel)
    }
}
