@testable import Bank_App
import XCTest

class IntroControllerTests: XCTestCase {
    // MARK: Subject under test
    
    var sut: IntroController!
    var window: UIWindow!
    
    // MARK: Test lifecycle
    
    override func setUp() {
        super.setUp()
        window = UIWindow()
        setupIntroViewController()
    }
    
    override func tearDown() {
        window = nil
        super.tearDown()
    }
    
    // MARK: Test setup
    
    func setupIntroViewController() {
        let bundle = Bundle.main
        let storyboard = UIStoryboard(name: "Main", bundle: bundle)
        sut = storyboard.instantiateViewController(withIdentifier: "IntroController") as? IntroController
    }
    
    func loadView() {
        window.addSubview(sut.view)
        RunLoop.current.run(until: Date())
    }
    
    // MARK: Test doubles
    
    class IntroBusinessLogicSpy: IntroBusinessLogic {
        
        var doSomethingCalled = false
        
        func loginUser(user: UserLogin) {
            doSomethingCalled = true
        }
        
        func tryAutoLogin() {
            doSomethingCalled = true
        }
        
        func resetStatusBar() {
            doSomethingCalled = true
        }
        
        func verifyFields(_ fields: [UITextField]) {
            doSomethingCalled = true
        }
        
    }
    
    // MARK: Tests
    
    func testTryAutoLogin() {
        // Given
        let spy = IntroBusinessLogicSpy()
        sut.interactor = spy
        
        // When
        loadView()
        
        // Do
        sut.tryAutoLogin()
        
        // Then
        XCTAssertTrue(spy.doSomethingCalled, "o método relativo ao tryAutoLogin() no interactor foi chamado")
    }
    
    func testLogin() {
        // Given
        let spy = IntroBusinessLogicSpy()
        sut.interactor = spy
        
        // When
        loadView()
        
        // Do
        sut.doLogin()
        
        // Then
        XCTAssertTrue(spy.doSomethingCalled, "o método relativo ao tryAutoLogin() no interactor foi chamado")
    }
}
