import UIKit

protocol LoginPresentationLogic {
    func presentLogin(response: LoginScene.Login.Response)
    func presentTransaction(response: LoginScene.Transaction.Response)
}

class LoginPresenter: LoginPresentationLogic {
    weak var viewController: LoginDisplayLogic?
    
    // MARK: Do something
    func presentLogin(response: LoginScene.Login.Response) {
        let viewModel = LoginScene.Login.ViewModel(login: response.login)
        viewController?.displayLoginData(viewModel: viewModel)
    }
    
    func presentTransaction(response: LoginScene.Transaction.Response) {
        let viewModel = LoginScene.Transaction.ViewModel()
        viewController?.displayTransaction(viewModel: viewModel)
    }
}
