import UIKit

@objc protocol LoginRoutingLogic {
    func routeToTransaction()
}

protocol LoginDataPassing {
    var dataStore: LoginDataStore? { get }
}

class LoginRouter: NSObject, LoginRoutingLogic, LoginDataPassing {
    weak var viewController: LoginViewController?
    var dataStore: LoginDataStore?
  
    // MARK: Routing
    func routeToTransaction(){
        let storyboard = UIStoryboard(name: "Main", bundle: nil)
        let destinationVC = storyboard.instantiateViewController(withIdentifier: "TransactionViewController") as! TransactionViewController
        var destinationDS = destinationVC.router!.dataStore!
        passDataToTransaction(source: dataStore!, destination: &destinationDS)
        navigateToTransaction(source: viewController!, destination: destinationVC)
    }

    // MARK: Navigation
    func navigateToTransaction(source: LoginViewController, destination: TransactionViewController) {
        source.show(destination, sender: nil)
    }
  
    // MARK: Passing data
    func passDataToTransaction(source: LoginDataStore, destination: inout TransactionDataStore) {
        destination.userId = source.userId
        destination.name = source.name
        destination.account = source.account
        destination.agency = source.agency
        destination.balance = source.balance
    }
}
