import UIKit

class LoginWorker {
    func fetchLogin(user: String, pass: String, parameters: [String: String], completionHandler: @escaping ([User]) -> Void) {
        NetworkManager.shared.getUser(user: user, pass: pass, parameters: parameters) { login in
            completionHandler(login)
        }
    }
}
