import UIKit

class TransactionWorker {
    func fetchTransactions(userId: Int, completionHandler: @escaping ([Transaction]) -> Void) {
        NetworkManager.shared.getTransactions(withUserId: "\(userId)") { transactions in
            completionHandler(transactions)
        }
    }
}
