import UIKit
import Foundation

protocol LoginDisplayLogic: class {
    func setupScreen(user: UserRealm)
    func displaySomething(viewModel: Login.ViewModel)
    func showAlertError(error: String)
}

class LoginViewController: BaseViewController {
    @IBOutlet weak var usernameTextField: UITextField!
    @IBOutlet weak var passwordTextField: UITextField!
    
    var router: LoginRouter
    private var interactor: LoginInteractor
    private var presenter: LoginPresenter

    init(interactor: LoginInteractor, router: LoginRouter, presenter: LoginPresenter) {
        self.interactor = interactor
        self.interactor.presenter = presenter
        self.presenter = presenter
        self.router = router
        super.init()
    }
    
    required init?(coder aDecoder: NSCoder) {
        fatalError("init(coder:) has not been implemented")
    }
    
    // MARK: Object lifecycle
    override func viewDidLoad() {
        super.viewDidLoad()
        setupView()
    }
    
  // MARK: Setup
    private func setupView() {
        presenter.viewController = self
        interactor.getLastUser()
    }

  // MARK: Do something
  
    @IBAction func actionLogin(_ sender: Any) {
        showLoading()
        let request = Login.Request(username: usernameTextField.text ?? "",
                                    password: passwordTextField.text ?? "")
        interactor.doSomething(request: request)
    }
    
    func actionLogin() {
        if let user = interactor.user {
            router.routeToSomewhere(login: self, user: user)
        }
    }
}

extension LoginViewController: LoginDisplayLogic {
    func showAlertError(error: String) {
        stopLoading()
        showError(err: error)
    }
    
    func displaySomething(viewModel: Login.ViewModel) {
        stopLoading()
        if let user = interactor.user {
            router.routeToSomewhere(login: self, user: user)
        }
    }
    
    func setupScreen(user: UserRealm) {
        usernameTextField.text = user.username
    }
}
