import UIKit

protocol LoginPresentationLogic
{
    func presentLogin(accountModel: Login.AccountModel)
    func showAlert(alertBodyMessage: String)
}

class LoginPresenter: LoginPresentationLogic
{
    
    weak var viewController: LoginDisplayLogic?
    
    
    
    // MARK: Display Login
    func presentLogin(accountModel: Login.AccountModel)
    {
        viewController?.displayLogin(accountModel: accountModel)
    }
    
    
    
    // MARK: Display Alert
    func showAlert(alertBodyMessage: String)
    {
        UIAlertController.showAlertAttention(msg: alertBodyMessage, view: viewController as! UIViewController)
    }
}
