import UIKit

protocol LoginPresentationLogic
{
    func presentLoginSucess(response: Login.UserData.Response)
    func presentError(error: Login.UserData.Error)
}

class LoginPresenter: LoginPresentationLogic
{
  weak var viewController: LoginDisplayLogic?
  
  // MARK: Do something
  
    func presentLoginSucess(response: Login.UserData.Response) {
        let viewModel = Login.UserData.ViewModel(id: response.id, name: response.name, agency: response.agency, account: response.account, balance: response.balance)
        viewController?.displaySuccessLogin(viewModel: viewModel)
    }
    
    func presentError(error: Login.UserData.Error) {
        viewController?.displayError(error: error)
    }
}
