import UIKit

protocol HomeBusinessLogic {
    func doSomething(request: Home.Request)
    func showAccount()
}

protocol HomeDataStore {
    var account: Login.UserAccount? { get set }
}

class HomeInteractor: HomeBusinessLogic, HomeDataStore {
   
  var presenter: HomePresentationLogic?
  var worker: HomeWorker?
  var account: Login.UserAccount?
  
  // MARK: Do something
    func showAccount() {
        let response = Home.Response(account: account)
        presenter?.presentAccount(response: response)
    }
    
    func doSomething(request: Home.Request) {
        worker = HomeWorker()
        worker?.doSomeWork()

        let response = Home.Response()
        presenter?.presentSomething(response: response)
    }
}
