import UIKit

protocol LoginRouterInput {
    
}

protocol LoginRouterDataSource: class {
    var userAccount: UserAccount! { get }
}

protocol LoginRouterDataDestination: class {
    
}

class LoginRouter: LoginRouterInput {
    
    weak var viewController: LoginViewController!
    weak private var dataSource: LoginRouterDataSource!
    weak var dataDestination: LoginRouterDataDestination!
    
    struct SegueIdentifiers {
        static let statements = "StatemetsSegue"
    }
    
    init(viewController: LoginViewController, dataSource: LoginRouterDataSource, dataDestination: LoginRouterDataDestination) {
        self.viewController = viewController
        self.dataSource = dataSource
        self.dataDestination = dataDestination
    }
    
    // MARK: Navigation
    
    func navigateLoginToStatementsScene(){
        viewController.performSegue(withIdentifier: SegueIdentifiers.statements, sender: viewController)
    }
    
    // MARK: Communication
    
    func passDataToNextScene(for segue: UIStoryboardSegue) {
        // NOTE: Teach the router which scenes it can communicate with
        if let statementViewController = segue.destination as? StatementViewController {
            statementViewController.router?.dataDestination.userAccount = dataSource.userAccount
        }
    }
}
