import UIKit

class LoginWorker
{
    func fetchUserID(user: String, pass: String, completionHandler: @escaping ([UserAccountDate]) -> Void){
        NetworkManager.shared.loginUser(user: user, pass: pass) { (useraccounts) in
            completionHandler(useraccounts)
        }
    }
}


