import UIKit

protocol StatementBusinessLogic
{
    func doLoadStatementData(request: StatementScene.Load.Request)
}

protocol StatementDataStore
{
    //var name: String { get set }
    var userId: Int { get set }
    var name: String { get set }
    var bankAccount: String {get set}
    var agency: String {get set}
    var balance: Double {get set}
}

class StatementInteractor: StatementBusinessLogic, StatementDataStore
{
    var presenter: StatementPresentationLogic?
    var worker: StatementWorker?
    //var name: String = ""
    var userId: Int = 0
    var name: String = ""
    var bankAccount: String = ""
    var agency: String = ""
    var balance: Double = 0
    
    
    // MARK: Do something
    
    func doLoadStatementData(request: StatementScene.Load.Request)
    {
        worker = StatementWorker()
        worker?.getDateStatements(userId: userId, completionHandler: { statements in
            let response = StatementScene.Load.Response(statements: statements)
            self.presenter?.presentStatementData(response: response)
        })        
        
    }
}
