import UIKit

protocol StatementPresentationLogic {
    func presentStatementData(response: StatementScene.Load.Response)
}

class StatementPresenter: StatementPresentationLogic {
    weak var viewController: StatementDisplayLogic?
    
    // MARK: Do something
    
    func presentStatementData(response: StatementScene.Load.Response) {
        let viewModel = StatementScene.Load.ViewModel(statements: response.statements)
        viewController?.displayStatementData(viewModel: viewModel)
    }
}
