import UIKit

protocol HomePresentationLogic
{
    func presentUserInfos(with user: User)
    func didFetchedUserStatements()
    func hideLoadingView()
    func showLoadingView()
}

class HomePresenter: HomePresentationLogic
{
    weak var viewController: HomeDisplayLogic?
    var statements: [Statements]?
    
    // MARK: Do something
    func presentUserInfos(with user: User) {
        guard let name = user.name,
            let agency = user.agency,
            let bankAccount = user.bankAccount,
            let balance = user.balance else {
            return
        }
        
        let viewModel = Home.Something.ViewModel(name: name,
                                                 bankAgencyAccount: bankAccount + " / " + agency,
                                                 balance: "R$\(balance)")
        
        viewController?.displayUser(viewModel: viewModel)
        
    }
    
    func hideLoadingView() {
        self.viewController?.hideLoadingView()
        
    }
    
    func showLoadingView() {
        self.viewController?.showLoadingView()
        
    }
    
    func didFetchedUserStatements() {
        self.viewController?.loadTableView()
    }
    
}
