import UIKit

@objc protocol AccountRoutingLogic {
  func routeToLogin()
}

protocol AccountDataPassing {
  var dataStore: AccountDataStore? { get }
}

final class AccountRouter: NSObject, AccountRoutingLogic, AccountDataPassing {
  weak var viewController: AccountViewController?
  var dataStore: AccountDataStore?

  func routeToLogin() {
    let viewController = LoginViewController()
    guard let accountViewController = self.viewController else { return }
    navigateToLogin(source: accountViewController, destination: viewController)
  }

  // MARK: Navigation

  func navigateToLogin(source: AccountViewController, destination: LoginViewController) {
    source.view.window?.transition(from: source, to: destination)
  }
}
