import UIKit
import Alamofire
import SwiftyJSON


class LoginWorker
{
    func doLoginRequest(user: String, password: String, completionHandler: @escaping (Bool, UserInfo?) -> Void)
  {
    let parameters = ["user": user, "password": password]
//    let parameters = ["user": "test_user", "password": "Test@1"]
    var userInfo = UserInfo()
    print("LoginWorker: doLoginRequest - user:\(user) pass:\(password)")
    Alamofire.request("https://bank-app-test.herokuapp.com/api/login", method: .post, parameters: parameters, encoding: URLEncoding.httpBody)
        .validate(statusCode: 200..<300)
        .validate(contentType: ["application/json"])
        .responseJSON(completionHandler: { (response) in
            guard response.result.isSuccess,
                let value = response.result.value else {
                    print("Error while fetching tags: \(String(describing: response.result.error))")
                    userInfo.userId = "-1"
                    userInfo.name = "Erro ao efetuar o Login, tente mais tarde!"
                    completionHandler(false, userInfo)
                    return
            }
            
            let json = JSON(value)
            if json["userAccount"].exists() {
                if json["userAccount"]["userId"].exists() {
                    userInfo.name = json["userAccount"]["name"].stringValue
                    userInfo.agency = json["userAccount"]["agency"].stringValue
                    userInfo.userId = json["userAccount"]["userId"].stringValue
                    userInfo.bankAccount = json["userAccount"]["bankAccount"].stringValue
                    userInfo.balance = json["userAccount"]["balance"].floatValue
                    completionHandler(true, userInfo)
                }
            }

            if json["error"].exists() {
                if json["error"]["code"].exists() {
                    userInfo.userId = "-1"
                    userInfo.name = json["error"]["message"].stringValue
                    completionHandler(false, userInfo)
                }
            }
        })
    }
}
