import UIKit

protocol StatementsPresentationLogic
{
    func presentSomething(response: Statements.StatementsApi.Response, userAccount : UserAcount)
}

class StatementsPresenter: StatementsPresentationLogic
{
  weak var viewController: StatementsDisplayLogic?
  
  // MARK: Do something
  
  func presentSomething(response: Statements.StatementsApi.Response ,userAccount : UserAcount)
  {
    let viewModel = Statements.StatementsApi.ViewModel(statement: response.statement)
    viewController?.displaySomething(viewModel: viewModel, userAccount: userAccount)
  }
}
