import UIKit

protocol LoginScenePresentationLogic
{
    func presentLogin(response: LoginScene.ReadLogin.Response)
    func presentMessage(response: LoginScene.ValidateLogin.Response)
    func presentClient(response: SetClient.Response)
}

class LoginScenePresenter: LoginScenePresentationLogic
{
  weak var viewController: LoginSceneDisplayLogic?
  
  // MARK: Return to view
  
    func presentLogin(response: LoginScene.ReadLogin.Response)
    {
        let viewModel = LoginScene.ReadLogin.ViewModel(login: response.login)
        viewController?.displayUser(viewModel: viewModel)
    }
    
    func presentMessage(response: LoginScene.ValidateLogin.Response)
    {
        let viewModel = LoginScene.ValidateLogin.ViewModel(alertMessage: response.alertMessage)
        viewController?.displayMessage(viewModel: viewModel)
    }
    
    func presentClient (response: SetClient.Response)
    {
        let viewModel = SetClient.ViewModel(client: response.client)
        viewController?.showAccount(viewModel: viewModel)
    }
}

