import UIKit

protocol LoginSceneDisplayLogic: class
{
    func displayUser(viewModel: LoginScene.ReadLogin.ViewModel)
    func displayMessage(viewModel: LoginScene.ValidateLogin.ViewModel)
    func showAccount(viewModel: SetClient.ViewModel)
}

class LoginSceneViewController: UIViewController, LoginSceneDisplayLogic
{
  var interactor: LoginSceneBusinessLogic?
  var router: (NSObjectProtocol & LoginSceneRoutingLogic & LoginSceneDataPassing)?

    // MARK: Outlets
    
    @IBOutlet weak var txtLogin: UITextField!
    @IBOutlet weak var txtPassword: UITextField!
    @IBOutlet weak var lblAlert: UILabel!
    @IBOutlet weak var bttLogin: UIButton!
    
  // MARK: Object lifecycle
  
  override init(nibName nibNameOrNil: String?, bundle nibBundleOrNil: Bundle?)
  {
    super.init(nibName: nibNameOrNil, bundle: nibBundleOrNil)
    setup()
  }
  
  required init?(coder aDecoder: NSCoder)
  {
    super.init(coder: aDecoder)
    setup()
  }
  
  // MARK: Setup
  
  private func setup()
  {
    let viewController = self
    let interactor = LoginSceneInteractor()
    let presenter = LoginScenePresenter()
    let router = LoginSceneRouter()
    viewController.interactor = interactor
    viewController.router = router
    interactor.presenter = presenter
    presenter.viewController = viewController
    router.viewController = viewController
    router.dataStore = interactor
  }
  
  // MARK: Routing
  
  override func prepare(for segue: UIStoryboardSegue, sender: Any?)
  {
    if let scene = segue.identifier {
      let selector = NSSelectorFromString("routeTo\(scene)WithSegue:")
      if let router = router, router.responds(to: selector) {
        router.perform(selector, with: segue)
      }
    }
  }
    
  // MARK: View lifecycle
  
    override func viewDidLoad()
    {
        super.viewDidLoad()
        // MARK: To hide the device keyboard 
        let tap = UITapGestureRecognizer(target: self, action: #selector(UIInputViewController.dismissKeyboard))
        view.addGestureRecognizer(tap)

        searchLogin()
        
        if bttLogin != nil {
            bttLogin.layer.cornerRadius = 8
        }
    }
    
    @objc func dismissKeyboard() {
        view.endEditing(true)
    }

    override func viewWillAppear(_ animated: Bool) {
        txtPassword.text = ""
    }
  
  // MARK: Searching for stored login
  
    func searchLogin()
    {
        let request = LoginScene.ReadLogin.Request()
        interactor?.readStoredLogin(request: request)
    }
  
    func displayUser(viewModel: LoginScene.ReadLogin.ViewModel)
    {
        txtLogin.text = viewModel.login
    }
    
    // MARK: Show alert messages
    
    func displayMessage(viewModel: LoginScene.ValidateLogin.ViewModel)
    {
        DispatchQueue.main.async {
            self.lblAlert.text = viewModel.alertMessage
        }
    }
    // MARK: Navigate to Account
    
    func showAccount(viewModel: SetClient.ViewModel) {
        DispatchQueue.main.async {
            self.lblAlert.text = ""
            self.performSegue(withIdentifier: "AccountScene", sender: nil)
        }

    }
    // MARK: Button clicked
    
    @IBAction func buttonTapped(_ sender: Any) {
        let request = LoginScene.ValidateLogin.Request(login: txtLogin.text!,
                                                       password: txtPassword.text!)
        interactor?.validateLogin(request: request)
    }
}




