import UIKit

@objc protocol LoginRoutingLogic
{
  func routeToStatementsList(segue: UIStoryboardSegue?)
}

protocol LoginDataPassing
{
  var dataStore: LoginDataStore? { get }
}

class LoginRouter: NSObject, LoginRoutingLogic, LoginDataPassing
{
  weak var viewController: LoginViewController?
  var dataStore: LoginDataStore?
  
  // MARK: Routing
  
  func routeToStatementsList(segue: UIStoryboardSegue?)
  {
    if let segue = segue {
      let destinationVC = segue.destination as! StatementsViewController
      var destinationDS = destinationVC.router!.dataStore!
      passDataToStatementsViewController(source: dataStore!, destination: &destinationDS)
    } else {
      let storyboard = UIStoryboard(name: "Main", bundle: nil)
      let destinationVC = storyboard.instantiateViewController(withIdentifier: "statementsViewController") as! StatementsViewController
      var destinationDS = destinationVC.router!.dataStore!
      passDataToStatementsViewController(source: dataStore!, destination: &destinationDS)
      navigateToStatementsViewController(source: viewController!, destination: destinationVC)
    }
  }

  // MARK: Navigation
  
  func navigateToStatementsViewController(source: LoginViewController, destination: StatementsViewController)
  {
    destination.modalPresentationStyle = .fullScreen
    source.show(destination, sender: nil)
  }
  
  // MARK: Passing data
  func passDataToStatementsViewController(source: LoginDataStore, destination: inout StatementsDataStore)
  {
    destination.userAccount = source.userAccount
  }
}
