import UIKit

enum LoginModel
{
    struct RequestLoginModel {
        
    }
    
    struct ResponseLoginModel: Decodable {
        let userAccount: UserModel?
    }
    
    struct UserModel: Decodable {
        var userId : Int?
        var name : String?
        var bankAccount : String?
        var agency : String?
        var balance : Float?
    }
  
}
