import UIKit

protocol AccountStatmentsBusinessLogic {
    func getUserAccountData(request: AccountStatments.UserAccountDescriptionData.Request)
    //    func getUserStatesData(request: AccountStatments.ShowStatements.Request)
}

protocol AccountStatmentsDataStore {
    var userAccount: UserAccountData! { get set }
}

class AccountStatmentsInteractor: AccountStatmentsBusinessLogic, AccountStatmentsDataStore{
    
    var userAccount: UserAccountData!
    var presenter: AccountStatmentsPresentationLogic?
    var worker: AccountStatmentsWorker?
            
    func getUserAccountData(request: AccountStatments.UserAccountDescriptionData.Request) {
        self.userAccount = request.userAccount
        let response = AccountStatments.UserAccountDescriptionData.Response(userAccount: self.userAccount)
        presenter?.presentUserInfoData(response: response)
    }
}
