import UIKit

enum Login {
    struct LoginLabes {
        var userName:String
        var password:String
    }
    // MARK: Use cases
    
    enum login
    {
        struct Request
        {
            var labels: LoginLabes
        }
        struct Response
        {
            var userResponse:UserAccountData?
            var error:UserDataErrorMessage?
        }
        struct ViewModel
        {
            var userAcoount:UserAccountData?
            var error:UserDataErrorMessage?
            
            init(user:UserAccountData?,error:UserDataErrorMessage) {
                self.userAcoount = user
                self.error = error
                
                if let e = self.error?.code{
                    if e  == 405 {
                        self.error?.message = "problemas ao busca seus dados,tente novamente."
                    }
                }
            }
        }
    }
    
    enum FetchLastUserSignIns {
        struct Request {}
        
        struct Response {
            let userName: String?
        }
        
        struct ViewModel {
            let userName: String
            
            init(userName: String?) {
                self.userName = userName ?? ""
            }
        }
    }
}
