import UIKit

@objc protocol LoginRoutingLogic{
  //func routeToSomewhere(segue: UIStoryboardSegue?)
}

protocol LoginDataPassing{
  var dataStore: LoginDataStore? { get }
}


class LoginRouter: NSObject, LoginRoutingLogic, LoginDataPassing {
  weak var viewController: LoginViewController?
  var dataStore: LoginDataStore?
  
  // MARK: Routing
  
  func routeToSomewhere(segue: UIStoryboardSegue?)
  {
    if let accountStatmentsViewController = viewController?.storyboard?.instantiateViewController(withIdentifier: "AccountStatmentsViewController") as? AccountStatmentsViewController {
        var destinationDS = accountStatmentsViewController.router!.dataStore!
    
        passDataFromLoginToAccountStatementsScreen(source: dataStore!, destination: &destinationDS)
        viewController?.navigationController?.pushViewController(accountStatmentsViewController, animated: true)
    }
  }

  // MARK: Navigation
  
  //func navigateToSomewhere(source: LoginViewController, destination: SomewhereViewController)
  //{
  //  source.show(destination, sender: nil)
  //}
  
  // MARK: Passing data
  
  func passDataFromLoginToAccountStatementsScreen(source: LoginDataStore!, destination: inout AccountStatmentsViewController) {
    destination. = source.loginUserAccountData
  }
}
