import UIKit

enum AccountDetails {
    // MARK: Use cases
  
    enum AccountInfo {
        struct Request {}
        
        struct Response {
            var user: User
        }

        struct ViewModel {
            var name: String
            var accountNumber: String
            var balance: String
        }
    }
    
    enum ListStatements {
        struct Request {}
        
        struct Response {
            var statements: [Statement]
            var success: Bool
        }

        struct ViewModel {
            var statements: [StatementViewModel]
            var success: Bool
        }

        struct StatementViewModel {
            var title: String
            var description: String
            var date: String
            var value: String
        }
    }
}
