import UIKit

@objc protocol LoginRoutingLogic {
    func routeToAccountDetails(segue: UIStoryboardSegue?)
}

protocol LoginDataPassing {
    var dataStore: LoginDataStore? { get }
}

class LoginRouter: NSObject, LoginRoutingLogic, LoginDataPassing {
    weak var viewController: LoginViewController?
    var dataStore: LoginDataStore?
  
    // MARK: Routing

    func routeToAccountDetails(segue: UIStoryboardSegue?) {
        if let segue = segue {
            let destinationViewController = segue.destination as! AccountDetailsViewController
            var destinationDataStore = destinationViewController.router!.dataStore!
            passDataToAccountDetails(source: dataStore!, destination: &destinationDataStore)
        }
    }

    // MARK: Passing data

    func passDataToAccountDetails(source: LoginDataStore, destination: inout AccountDetailsDataStore) {
        destination.user = source.user
    }
}
