import Alamofire
import SwiftyJSON
import UIKit

protocol LoginWorkerLogic {
    func login(user: String?, password: String?, _ completion: @escaping ((_ user: User?, _ error: Bool?) -> Void))
}

class LoginWorker: LoginWorkerLogic {
    func login(user: String?, password: String?, _ completion: @escaping ((_ user: User?, _ error: Bool?) -> Void)) {
        guard let url = API.getURL(.login), let user = user, let password = password else {
            return completion(nil, true)
        }

        let parameters = [
            "user": user,
            "password": password
        ]

        let request = Alamofire.request(url, method: .post, parameters: parameters, encoding: JSONEncoding.default, headers: API.defaultHeaders)

        request.responseJSON { response in
            switch response.result {
            case .success(let data):
                let json = JSON(data)
                let userAccount = json["userAccount"]
                let error = json["error"]["message"]

                if (error != JSON.null) {
                    return completion(nil, true)
                }

                return completion(User.fromJSON(json: userAccount), nil)
            case .failure(_):
                return completion(nil, true)
            }
        }
    }
}
